import { Component } from "react";
import Result from "./Result";

function randomnumber(){
    let a=Math.random()*100;
    return Math.floor(a);
}

function checkgues(){
    var ran=randomnumber();
    var userguestext=document.getElementById("gues").value;
    document.getElementById("gues").style.backgroundColor="red";
    var gues=Number(userguestext);
   
    if(ran==userguestext){
        
        document.getElementById("qlue").innerHTML="you are correct"
    }
    else if (ran>userguestext) {
       var v= document.getElementById("qlue");
       v.innerHTML="gues is high";
       v.style.backgroundColor="red";
    } 
    else {
        var v= document.getElementById("qlue");

       v.innerHTML="gues is low";
       v.style.backgroundColor="black";
       v.style.color="white";
    }
}

class Guesnumber extends Component{
    render(){
        return(
        <div className="gues">
            <form onLoad={randomnumber}>
            <label>Enter Number</label><br/>
            <input id="gues" className="input" type="text" placeholder="Enter the name"/>
            <br>
            </br>
            <br></br>
            <div id="qlue"></div>
           <button onClick={checkgues} >Check</button><br/>
           <br/>
           <button>Restart</button>
           </form>
           
        </div>
            
        );
    }
}
export default Guesnumber;
