import Main from "./Main";
import React from "react";
import ReactDOM from 'react-dom/client';
import Results from "./Results";


export default function Guesnum(props){
     
     const Check=(a)=>{
        var usergues=document.getElementById('inp1').value;
        document.getElementById('inp1').value="";
        if(a==usergues){
            document.getElementById("add").innerHTML="you are correct";
            document.getElementById("add").style.color="white";
            document.getElementById("add").style.backgroundColor="green";
        }
        else if(a<usergues){
            document.getElementById("add").innerHTML="gues is too high";
            document.getElementById("add").style.color="white";
            document.getElementById("add").style.backgroundColor="red";
        }
        else{
            document.getElementById("add").innerHTML="gues is too low";
            document.getElementById("add").style.color="white";
            document.getElementById("add").style.backgroundColor="red";

        }
        
    }
        const Restart=()=>{
            const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <Main/>
  </React.StrictMode>
);

        
        }

      
        
     
    return(
    <div>
        <div className="in">
           <input  className="input" id="inp1" type="text" placeholder="Enter Number"/>
           <br></br><br/>
           <div id="add"></div>
           <br/>
           <button onClick={()=>Check(props.ran)}>Check</button>
           <br></br><br/>
           <button onClick={()=>Restart()}>Restart</button>
        </div>
        <Results/>
        
    </div>
    );
}
